import React, {useState} from 'react';
import DatePicker from "react-datepicker";
import {Calendar, dateFnsLocalizer} from "react-big-calendar";
import format from "date-fns/format";
import parse from "date-fns/parse";
import startOfWeek from "date-fns/startOfWeek";
import getDay from "date-fns/getDay";
import "react-big-calendar/lib/css/react-big-calendar.css";
import moment from 'moment';

const locales = {
    "ko": {
    localize: {
        month: () => '월',
        day: () => '일',
        today: '오늘'
    },
    format: {
        dateFormat: 'P',
        timeFormat: 'p',
        dateTimeFormat: 'Pp',
        dayFormat: 'D',
        weekdayFormat: 'ccc',
        monthHeaderFormat: 'yyyy년 MMMM',
        dayHeaderFormat: 'yyyy년 MMMM dd일 EEE',
        agendaDateFormat: 'P',
        agendaTimeFormat: 'p',
        agendaTimeRangeFormat: 'P',
    },
    },
    "ko-KR": {
    localize: {
        month: () => '월',
        day: () => '일',
        today: '오늘'
    },
    format: {
        dateFormat: 'P',
        timeFormat: 'p',
        dateTimeFormat: 'Pp',
        dayFormat: 'D',
        weekdayFormat: 'ccc',
        monthHeaderFormat: 'yyyy년 MMMM',
        dayHeaderFormat: 'yyyy년 MMMM dd일 EEE',
        agendaDateFormat: 'P',
        agendaTimeFormat: 'p',
        agendaTimeRangeFormat: 'P',
    },
    }
};

const localizer = dateFnsLocalizer({
    format,
    parse,
    startOfWeek,
    getDay,
    locales
})


const events = [
    {
        title: "Big Meeting",
        allDay: true,
        start: new Date(),
        end: new Date()
    },
    {
        title: "Big Meeting",
        allDay: true,
        start: new Date(),
        end: new Date()
    },
    {
        title: "Conference",
        start: new Date(),
        end: new Date()
    }
]

export default function IddaeMohae(){
    return(
        <>
        <div>
            <Calendar localizer={localizer} events={events} 
            startAccessor="start" endAccessor="end"
            style={{height: 500, margin: "50px"}} culture="ko" />
        </div>
        </>
    )
}
